package main

import (
	"testing"

	"github.com/stretchr/testify/require"
)

func TestGetMostCommonBitOccurrence(t *testing.T) {
	input := "NCNBCHB"
	expectedResult := 2

	p := Polymer{polymer: input}
	actualResult := p.getMostCommonBitOccurrence()
	require.Equal(t, expectedResult, actualResult)
}

func TestGetLeastCommonBitOccurrence(t *testing.T) {
	input := ""
	expectedResult := 161

	p := Polymer{polymer: input}
	actualResult := p.getLeastCommonBitOccurrence()
	require.Equal(t, expectedResult, actualResult)
}

func TestPolymerize(t *testing.T) {
	tests := []struct {
		name         string
		inputPolymer string
		inputRules   []string
		result       string
	}{
		{
			name:         "test 0",
			inputPolymer: "NNCB",
			inputRules: []string{
				"CH -> B",
				"HH -> N",
				"CB -> H",
				"NH -> C",
				"HB -> C",
				"HC -> B",
				"HN -> C",
				"NN -> C",
				"BH -> H",
				"NC -> B",
				"NB -> B",
				"BN -> B",
				"BB -> N",
				"BC -> B",
				"CC -> N",
				"CN -> C",
			},
			result: "NCNBCHB",
		},
		{
			name:         "test 1",
			inputPolymer: "NBBBCNCCNBBNBNBBCHBHHBCHB",
			inputRules: []string{
				"CH -> B",
				"HH -> N",
				"CB -> H",
				"NH -> C",
				"HB -> C",
				"HC -> B",
				"HN -> C",
				"NN -> C",
				"BH -> H",
				"NC -> B",
				"NB -> B",
				"BN -> B",
				"BB -> N",
				"BC -> B",
				"CC -> N",
				"CN -> C",
			},
			result: "NBBNBNBBCCNBCNCCNBBNBBNBBBNBBNBBCBHCBHHNHCBBCBHCB",
		},
	}

	for _, tc := range tests {
		t.Run(tc.name, func(t *testing.T) {
			p := newPolymer(tc.inputPolymer, tc.inputRules)
			p.polymerize()
			require.Equal(t, tc.result, p.polymer)
		})
	}

}
